import React from 'react';
import './CardMovie.css';

const CardMovie = ({ movie }) => {
  return (
    <div containerCard>
      <img 
        className="imgCharacter"
        src={movie.posterUrl}/>
        <p>
          Movie:{movie.title}<br/>
          Director : {movie.director} <br/> 
          Year : {movie.year}  <br/>
          Country : {movie.country}
        </p>
    </div>
  )
}

export default CardMovie
